import React, {useState} from "react";

function Apitest() {
    const [user, setUser] = useState({login: '', password: ''})
    const [accessToken, setToken] = useState()
    let users
    function loginClick() {
        const requestOptions = {
            method: 'POST',
            headers: {'Content-Type': 'application/json'},
            body: JSON.stringify({login: user.login, password: user.password})
        };
        fetch('http://localhost:8000/api/v1/login', requestOptions)
            .then(response => response.json())
            .then(data => setToken(data.access_token));
    }

    function getUsers() {
        const headers = { 'Authorization': 'Bearer ' + accessToken}
        fetch('http://localhost:8000/api/v1/users', { headers })
            .then(response => response.json())
            .then(data => users = data.data);
    }

    const handleChange = (e) => {
        setUser({...user, [e.target.name]: e.target.value});
    };

    return (
        <div>
            <div>
                <label htmlFor={'login'}>Name:</label>
                <input id='login' name={'login'} onChange={handleChange}/>
                <label htmlFor={'password'}>{user.password}:</label>
                <input id='password' name={'password'} type={'password'} onChange={handleChange}/>
                <button onClick={loginClick}>Login</button>
            </div>
            <div>
                {users}
                <button onClick={getUsers}>Login</button>
            </div>
        </div>
    )
}


export default Apitest
