import React, {useState} from "react";
import "./api.css"

function Apitest() {
    const [user, setUser] = useState({login: '', password: ''})
    const [users, setUsers] = useState()

    async function loginClick() {
        const requestOptions = {
            method: 'POST',
            headers: {'Content-Type': 'application/json'},
            body: JSON.stringify({login: user.login, password: user.password})
        };
        const response = await fetch('http://localhost:8000/api/v1/login', requestOptions)
        if (response.ok) {
            const data = await response.json()
            localStorage.setItem('token', data.access_token)
        } else {
            alert("Login data is incorrect")
        }
    }

    async function getUsers() {
        const headers = {'Authorization': 'Bearer ' + localStorage.getItem('token')}
        const response = await fetch('http://localhost:8000/api/v1/users', {headers})
        if (response.ok) {
            const data = await response.json()
            setUsers(data.data)
        } else {
            alert("You don't have enough rights")
        }
    }

    const handleChange = (e) => {
        setUser({...user, [e.target.name]: e.target.value});
    };

    return (
        <div>
            <div className={"register"}>
                <span>Register</span>
                <label htmlFor={'reg_login'}>Name</label>
                <input id='reg_login' name={'reg_login'} onChange={handleChange}/>
                <label htmlFor={'reg_password'}>Password: {user.password}</label>
                <input id='reg_password' name={'reg_password'} type={'password'} onChange={handleChange}/>
                <label htmlFor={'reg_role'}>Role</label>
                <input id='reg_role' name={'reg_role'} type={'number'} max={1} min={0} defaultValue={0} onChange={handleChange}/>
                <button onClick={loginClick}>Register</button>
            </div>
            <div className={"login"}>
                <span>Login</span>
                <label htmlFor={'login'}>Name:</label>
                <input id='login' name={'login'} onChange={handleChange}/>
                <label htmlFor={'password'}>Password: {user.password}</label>
                <input id='password' name={'password'} type={'password'} onChange={handleChange}/>
                <button onClick={loginClick}>Login</button>
            </div>
            <div>
                <ul>
                    {users ? users.map(user => (
                        <div>
                            <li>{user.ID} {user.Login} {user.Role}</li>
                        </div>
                    )) : ""}
                </ul>
                <button onClick={getUsers}>Get users</button>
            </div>
        </div>
    )
}


export default Apitest
