import './auth.css';
import {useDispatch, useSelector} from "react-redux";
let users = []
let dispatch;
function Auth(){
    users = useSelector((state)=>state.user.users)
    dispatch = useDispatch()
    return(
        <div className={"authBox"}>
            <input type="text" id="login" placeholder={"Login"}/>
            <input type="text" id="password" placeholder={"Password"}/>
            <input type="text" id="rights" placeholder={"Rights"}/>
            <label htmlFor={"role"}>Admin?<input type="checkbox" id="role"/></label>
            <input type="button" id="auth" value={"Login/Register"} onClick={StoreUser}/>
        </div>
    )
}

function StoreUser(){
    let login = document.getElementById("login")
    let password = document.getElementById("password")
    let role = document.getElementById("role")
    let rights = document.getElementById("rights")
    if (users.find(u => u.login === login)){
        console.log(users)
    }
}

export default Auth

export const isAuthenticated = user => !!user;

export const isAllowed = (user, rights) =>
    rights.some(right => user.rights.includes(right));
